import React, { useEffect, useState } from 'react'
import { Card, Row, Col, Container } from "react-bootstrap";

export default function Dynamic() {
    //https://nba-players.herokuapp.com/players-stats

    const [Employee, setEmployee] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('/Sample-employee-JSON-data.json')
            const nbaData = await response.json()
            setEmployee(nbaData.slice(0,2))
        }
        fetchData()
    }, [])
    return (
        <Container>
            <Row>
                {Employee.map((Employee, k) => (
                    <Col key={k} xs={12} md={4} lg={3}>
                        <Card >
                            <Card.Img src="https://picsum.photos/150?random=2" />

                            <Card.Body>
                                <Card.Title>{Employee.userId}</Card.Title>
                                <Card.Text>{Employee.firstName}</Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    )
}
